<?php
namespace Fhm\SliderBundle\Repository;

use Fhm\FhmBundle\Repository\FhmRepository;
use Doctrine\ODM\MongoDB\DocumentManager;
use Doctrine\ODM\MongoDB\UnitOfWork;
use Doctrine\ODM\MongoDB\Mapping\ClassMetadata;

/**
 * SliderRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SliderItemRepository extends FhmRepository
{
    /**
     * Constructor
     */
    public function __construct(DocumentManager $dm, UnitOfWork $uow, ClassMetadata $class)
    {
        parent::__construct($dm, $uow, $class);
    }

    /**
     * @param string $search
     * @param int    $page
     * @param int    $count
     * @param string $grouping
     *
     * @return mixed
     * @throws \Doctrine\ODM\MongoDB\MongoDBException
     */
    public function getFrontIndex($search = "", $page = 1, $count = 5, $grouping = "")
    {
        $builder = (($page > 0 && $count > 0) && $search) ? $this->search($search) : $this->createQueryBuilder();
        // Grouping
        if($grouping != "")
        {
            $builder->addOr($builder->expr()->field('grouping')->in((array) $grouping));
            $builder->addOr($builder->expr()->field('share')->equals(true));
        }
        // Pagination
        if($page > 0 && $count > 0)
        {
            $builder->limit($count);
            $builder->skip(($page - 1) * $count);
        }
        // Dates
        $builder->addAnd(
            $builder->expr()
                ->addOr($builder->expr()->field('date_start')->equals(null))
                ->addOr($builder->expr()->field('date_start')->lt(new \DateTime()))
        );
        $builder->addAnd(
            $builder->expr()
                ->addOr($builder->expr()->field('date_end')->equals(null))
                ->addOr($builder->expr()->field('date_end')->gt(new \DateTime()))
        );
        // Common
        $builder->field('active')->equals(true);
        $builder->field('delete')->equals(false);
        $this->builderSort($builder);

        return $builder
            ->getQuery()
            ->execute()
            ->toArray();
    }

    /**
     * @param string $search
     * @param string $grouping
     *
     * @return int
     * @throws \Doctrine\ODM\MongoDB\MongoDBException
     */
    public function getFrontCount($search = "", $grouping = "")
    {
        $builder = ($search) ? $this->search($search) : $this->createQueryBuilder();
        // Grouping
        if($grouping != "")
        {
            $builder->addOr($builder->expr()->field('grouping')->in((array) $grouping));
            $builder->addOr($builder->expr()->field('share')->equals(true));
        }
        // Dates
        $builder->addAnd(
            $builder->expr()
                ->addOr($builder->expr()->field('date_start')->equals(null))
                ->addOr($builder->expr()->field('date_start')->lt(new \DateTime()))
        );
        $builder->addAnd(
            $builder->expr()
                ->addOr($builder->expr()->field('date_end')->equals(null))
                ->addOr($builder->expr()->field('date_end')->gt(new \DateTime()))
        );
        // Common
        $builder->field('active')->equals(true);
        $builder->field('delete')->equals(false);

        return count($builder
            ->getQuery()
            ->execute()
            ->toArray());
    }

    /**
     * @param \Fhm\SliderBundle\Document\Slider $slider
     * @param string                            $search
     * @param int                               $page
     * @param int                               $count
     *
     * @return mixed
     * @throws \Doctrine\ODM\MongoDB\MongoDBException
     */
    public function getByGroupIndex(\Fhm\SliderBundle\Document\Slider $slider, $search = "", $page = 1, $count = 5)
    {
        $builder = (($page > 0 && $count > 0) && $search) ? $this->search($search) : $this->createQueryBuilder();
        // Global
        if($slider->getAddGlobal())
        {
            $builder->addAnd(
                $builder->expr()
                    ->addOr($builder->expr()->field('sliders.id')->equals($slider->getId()))
                    ->addOr($builder->expr()->field('global')->equals(true))
            );
        }
        else
        {
            $builder->field('sliders.id')->equals($slider->getId());
        }
        // Pagination
        if($page > 0 && $count > 0)
        {
            $builder->limit($count);
            $builder->skip(($page - 1) * $count);
        }
        // Common
        $builder->field('active')->equals(true);
        $builder->field('delete')->equals(false);
        $builder->sort($slider->getSortField(), $slider->getSortOrder());

        return $builder
            ->getQuery()
            ->execute()
            ->toArray();
    }

    /**
     * @param \Fhm\SliderBundle\Document\Slider $slider
     * @param string                            $search
     *
     * @return mixed
     */
    public function getByGroupCount(\Fhm\SliderBundle\Document\Slider $slider, $search = "")
    {
        $builder = ($search) ? $this->search($search) : $this->createQueryBuilder();
        // Global
        if($slider->getAddGlobal())
        {
            $builder->addAnd(
                $builder->expr()
                    ->addOr($builder->expr()->field('sliders.id')->equals($slider->getId()))
                    ->addOr($builder->expr()->field('global')->equals(true))
            );
        }
        else
        {
            $builder->field('sliders.id')->equals($slider->getId());
        }
        // Common
        $builder->field('active')->equals(true);
        $builder->field('delete')->equals(false);

        return $builder
            ->count()
            ->getQuery()
            ->execute();
    }

    /**
     * @param \Fhm\SliderBundle\Document\Slider $slider
     *
     * @return mixed
     * @throws \Doctrine\ODM\MongoDB\MongoDBException
     */
    public function getByGroupAll(\Fhm\SliderBundle\Document\Slider $slider)
    {
        $builder = $this->createQueryBuilder();
        // Global
        if($slider->getAddGlobal())
        {
            $builder->addAnd(
                $builder->expr()
                    ->addOr($builder->expr()->field('sliders.id')->equals($slider->getId()))
                    ->addOr($builder->expr()->field('global')->equals(true))
            );
        }
        else
        {
            $builder->field('sliders.id')->equals($slider->getId());
        }
        // Common
        $builder->field('active')->equals(true);
        $builder->field('delete')->equals(false);
        $builder->sort($slider->getSortField(), $slider->getSortOrder());

        return $builder
            ->getQuery()
            ->execute();
    }
}